void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    float size = 25.;
    float contrast = (cos(iTime) + 1.) * 10.;
    float overdrive = 0.03;

    float c = abs(mod((fragCoord.x / size), 2.) - 1.);
    c += abs(mod((fragCoord.y / size), 2.) - 1.);
    c /= 2.;

    c += overdrive;

    c = pow(c, contrast);

    fragColor = vec4(vec3(c), 1.0);
}
