#define PI 3.14159

// SMOOTH OR FLAT
const bool SMOOTH = true;
float smf(in float v,in float fac) {
    if(SMOOTH) return v;
    return floor(v*fac)/fac;
}

float norm(in float v) {
    if(v<0.f) {
        v = floor(abs(v))-abs(v);
    }
    return mod(v,1.f);
}
bool dlt(in float a,in float b,in float dist) {
    return abs((100.f+a) - (100.f+b))<dist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 cp = iResolution.xy/2.f;

    float angle = norm((atan(fragCoord.y-cp.y,fragCoord.x-cp.x)/PI+1.f)/2.f+.25f);

    float dist = sqrt(pow(fragCoord.x-cp.x,2.f)+pow(fragCoord.y-cp.y,2.f));

    float timeInSec = iDate.w;

    float sec = mod(timeInSec,60.f);
    float mnt = mod(timeInSec/60.f,60.f);
    float hor = mod(timeInSec/3600.f,12.f);


    if(dist < 100.f) {
        // HOUR DASHES
        if(mod(angle*12.f,1.f) < 0.1f && dist > 90.f) {
            fragColor = vec4(0.f);
            return;
        }
        // MINUTE DASHES
        if(mod(angle*60.f,1.f) < 0.075f && dist > 95.f) {
            fragColor = vec4(0.f);
            return;
        }
        bool secHandleVisible =  dlt(angle,smf(1.f-sec/60.f,60.f),.5f/dist) && dist < 95.f;
        bool mntHandleVisible =  dlt(angle,smf(1.f-mnt/60.f,60.f),.5f/dist) && dist < 75.f;
        bool horHandleVisible =  dlt(angle,smf(1.f-hor/12.f,12.f),.5f/dist) && dist < 60.f;


        // Output to screen
        fragColor = vec4(
            (secHandleVisible?0.f:1.f) *
            (mntHandleVisible?0.f:1.f) *
            (horHandleVisible?0.f:1.f)
        );
    }else if(dist < 105.f) {
        fragColor = vec4(0.f);
    }

}
