#define res iResolution.xy
#define t iTime

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec3 color;
	float len,z=t;
	for(float i=0.;i<3.;i++) {
		vec2 uv,p=fragCoord.xy/res.xy;
		uv=p;
		p-=.5;
		p.x*=(res.x/res.y);
		z+=0.2;
		len=length(p);
		uv=p+len*(sin(z)+0.9);
        uv+=abs(sin((len*9.)));
		color[0]=0.01/length(pow(uv.y,0.5)-0.5); 
	}
	fragColor=vec4(color/len,1.);
}
