// Fork of "10 PRINT inspired maze" by CanadianWarship. https://shadertoy.com/view/NtcyD7
// Modified to look best as a desktop wallpaper: 
//  slower to evolve, darker, slowly scrolling
// 2022-08-07 17:42:59

#define CellNumber 20. // how many cells span the shorter side, any float
#define Bias -0. // how frequent fwd slashes are relative to back slashes, [-0.5 ~ 0.5] default 0.
#define Thickness 0.2 // how thick the slashes are, [0. ~ 1.] default 0.2
#define TimeFactor 0.01 // how quickly the picture evolves, any float, default 0. or 0.05

float random( vec2 n ){ // https://thebookofshaders.com/10/
    return fract(sin(dot(n.xy, vec2(12.9898, 78.233))) * 
        (43758.5453 + TimeFactor*iTime)); // default prng has no iTime
}

float slash (in vec2 position, vec2 uv){ //generates slash given cell coordinate and cell uv
    float direction = random(position), result; // random float [0. ~ 1.]
    // if (random(abs(direction + vec2(1.))) > .4)
    //     return 0.; 
    if(abs(direction - .5) < 0.2) // amount of black, 0.05 is connected, 0.2 is balanced, 0.45 is very dark
        return 0.;
    else if
     (direction > 0.5 - Bias) //choose cell orientation
        abs(uv.x - uv.y) < Thickness ? result = 1. : result; // downleft - upright : fwd slash
    else
        abs(uv.x + uv.y - 1.) < Thickness ? result = 1. : result; // upleft - downright : backslash
    return result;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord.xy / min(iResolution.x, iResolution.y); // uv coordinates
    uv += vec2(0.0117, 0.00420)*iTime; // slow scrolling
    //uv *= vec2(-707.,1.);
    uv *= mat2(.707, -.707, .707, .707);
    vec2 pos = floor(uv * CellNumber); // cell coordinates
    vec2 celluv = fract(uv*CellNumber); // uv for each cell
    float result = slash(pos, celluv); // draw slashes
    float check2 = 0. ;

    if (random(pos) > 0.5 - Bias) check2 = 1.;
    float checkerboard =  mod ( (pos.x + pos.y)  , 2. );
    checkerboard = mod (checkerboard + check2 , 2.);
fragColor = vec4( 0.5*result*checkerboard , 0.3*result , 0.0 , 1.0);
}
