#define maxIt 240

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 fsize   = iResolution.xy;
    vec2 scaled  = fragCoord;
    scaled.x = ( scaled.x - fsize.x*(3./4.) / 2.0 ) * fsize.x;
    scaled.y = ( scaled.y - fsize.y / 2.0 ) * fsize.x;
    float pi = 3.14159265359;
    float fx = fragCoord.x/fsize.x;
    float mg = pow(2.,fragCoord.y/fsize.y*16.+8.);
    float an = (fx-3./4.)*8.*pi;
    float  x = fx<3./4.?scaled.x:mg*sin(an);
    float  y = fx<3./4.?scaled.y:mg*cos(an);
    float  t = iTime;
    float ox = -1.7686343663753872;
    float oy =  0.0021018480275678;
    float zm = pow(2.,-mod(t,25.)/25.*24.)*4e-5;
    float cx = x*zm + ox;
    float cy = y*zm + oy;
    float zx = 0.;
    float zy = 0.;
    float zzx, zzy, zzl;
    float a,b,c,u,R,G,B;

    for(int it = 0; it < maxIt; it++){
      zzx = zx * zx;
      zzy = zy * zy;
      if ( zzx + zzy > 4. && it > 1){
        a = log(zzl    );
        b = log(4.     );
        c = log(zzx+zzy);
        u = sqrt(float(it) + (b-a)/(c-a) + 1.) * 1. + t;
        float l =abs(sin((u/4.       )*pi*2.)           )*255.       ;
        float s =   (sin((u          )*pi*2.)+        1.)*0.5        ;
        R = (max(min(sin((u/16.      )*pi*2.)+0.5,1.),0.)*s+(1.-s))*l;
        G = (max(min(sin((u/16.+1./3.)*pi*2.)+0.5,1.),0.)*s+(1.-s))*l;
        B = (max(min(sin((u/16.+2./3.)*pi*2.)+0.5,1.),0.)*s+(1.-s))*l;
        fragColor = vec4(R/255.,G/255.,B/255., 255);
        return;
      }
      zzl = zzx + zzy;
      zy = 2. * zx * zy + cy;
      zx = zzx - zzy + cx;
    }
    fragColor = vec4(0,0,0,1.0);
}
