vec3 normcolor ( in vec3 a) {
    vec3 b;
    b = a /255.0;
    return(b);
    }


void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float posx = .5;
    float width = 0.02;
    float glowsize = 0.5;

    width -= 0.04 * abs(cos(iTime * 1.02)) -.02; // variation in scanline width

    // Time varying pixel color
    // colo = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec3 colo = normcolor (vec3(0.0, 20, 20));
    vec3 colo2 = normcolor (vec3(0, 0, 0));

    posx += 0.5 * cos(iTime); // scanline movement

    if((uv.x > posx-width)&&(uv.x < posx+width)) colo2 = normcolor(vec3(219.,18.,75.));

    // colo2 = vec3(smoothstep(width, width-glowsize, posx));

    // Output to screen
    fragColor = vec4(colo, 1.0);
    fragColor += vec4(colo2, 1.0);

    }
