void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy / iResolution.xy;
    vec2 q = p-vec2(0.5,0.5);

    vec3 col = mix(vec3(1.0, 0.0, 0.9), vec3(0.7, 0.0, 0.9),sqrt(p.x*p.y));

    float r = 0.2 + 0.3*cos(atan(q.y,q.x)*1000.0+1000.0*q.x+10.0);
    col *= smoothstep(r, r+0.01, length(q));

    r=0.015;
    col *= 1.0 - (1.0-smoothstep(r,r+0.001, abs(q.x+sin(20.0*q.y))))*(1.0-smoothstep(0.0,0.7,q.y));

    fragColor = vec4(col, 1.0);
}
