void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col;

    float offsetX = sin(iTime) * 100.0f;

    if (fragCoord.x < iResolution.x * 0.25f + offsetX || fragCoord.x > iResolution.x * 0.75f + offsetX)
    {
        col = vec3(fragCoord.x/iResolution.x, fragCoord.y/iResolution.y, sin(iTime));
    }
    else
    {
        col = vec3(fragCoord.x/iResolution.x + 0.3f, fragCoord.y/iResolution.y, cos(iTime / 0.25f));
    }

    if (fragCoord.y > floor(sin(iTime * 2.0f) * (iResolution.y / 2.0f) + iResolution.y / 2.0f))
    {
        col = vec3(cos(iTime / 0.25f), 0.0f, 0.0f);
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}
