float luma(vec4 color) {
  return dot(color.rgb, vec3(0.299, 0.587, 0.114));
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float puntoDeCorte =sin(iTime*0.5)*0.5+0.5;
    vec2 uv = fragCoord/iResolution.xy;
    float px =1./iChannelResolution[1].x;

    // Time varying pixel color

    vec3 col = texture(iChannel1, uv).rgb;

    float sum =0.;

    sum +=step(puntoDeCorte,luma(texture(iChannel1, uv +vec2(-px,0))));
    sum +=step(puntoDeCorte,luma(texture(iChannel1, uv +vec2(0,px))));
    sum +=step(puntoDeCorte,luma(texture(iChannel1, uv +vec2(0,-px))));
    sum +=step(puntoDeCorte,luma(texture(iChannel1, uv +vec2(px,0))));

    sum +=step(puntoDeCorte,luma(texture(iChannel1, uv +vec2(-px,px))));
    sum +=step(puntoDeCorte,luma(texture(iChannel1, uv +vec2(px,px))));
    sum +=step(puntoDeCorte,luma(texture(iChannel1, uv +vec2(px,-px))));
    sum +=step(puntoDeCorte,luma(texture(iChannel1, uv +vec2(-px,-px))));


    if(sum>0.)col= vec3(1.);


    fragColor = vec4(col,1.0);
}
