vec3 palette(float t){
    vec3 a = vec3(0.11,0.176,0.357);
    vec3 b = vec3(0.106,0.29,0.529);
    vec3 c = vec3(0.18,0.486,0.898);
    vec3 d = vec3(0.18,0.486,0.898);

    return a + b*cos(.28318*(c+t+d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;


    float d = abs(length(uv.y));

    vec3 col = palette(d) * (sin(iTime) + 2.);

    d+= length(uv.x) * -abs(uv.x) * (cos(iTime * .1) + 1.);

    d = abs(d);

    d = ((cos(iTime * .5)+1.2) * .003)/d;

    col *= d;

    // Output to screen
    fragColor = vec4(col, 1.0);
}
