void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=4.*(fragCoord.xy-.5*iResolution.xy)/iResolution.y;
    float s=1.,t=iTime;fragColor=vec4(s);
    for(int c=-1;c<3;fragColor[c-1]=smoothstep(.02,.01,length(uv-(s=1.+sin((t+=float(++c)*.025)-sqrt(length(uv))))*round(uv/s))-.01));
}
