void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = fragCoord.xy / iResolution.xy;
    vec2 q = p - vec2(0.3, 0.7);
    vec3 col = mix(vec3 ( 1.0, 0.4, 1.0 ), vec3(0.3, 0.3, 1.0), sqrt(p.y)*0.8);
    const float columns = 80.0;
    const float colWidth = 1.0/columns;

    //trunk
    float r = 0.015;
    float trunk = 1.0;
    r += 0.0015*cos(90.0*q.y);
    r += exp(-90.0*p.y);
    trunk -= (1.0 - smoothstep(r, r, abs(q.x-0.2*sin(2.0*q.y)) )) * (1.0 - smoothstep(0.0, 0.0, q.y));
    col *= trunk;
    col += (1.0 - trunk) * vec3(0.5, 0.2, 0.2);

    //flower
    r = 0.2 + 0.1*cos(atan(q.y, q.x) * 20.0 + 40.0 * (p.x + p.y) + iTime * 25.0);
    col *= smoothstep(r, r+0.01, length(q));
    col += (1.0 - smoothstep(r, r+0.01, length(q)))*vec3(1.0, 1.0, 0);


    fragColor = vec4(col, 1.0);
}
