void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    // Output to screen
    vec2 vect = vec2(uv.x - .5, uv.y - .5);
    //float angle = abs(atan(vect.y, vect.x));
    float angle = atan(vect.y, vect.x);
    vec4 m = iMouse / iResolution.x;
    float dist = sqrt(abs(uv.x - .5)*abs(uv.x - .5) + abs(uv.y - .5)*abs(uv.y - .5));
    angle = angle*20.;
    angle += iTime*20.;
    angle = (cos(angle + dist*100.) + 1.) * 180.;
    float iangle = float(int(angle) % 360);
    fragColor = vec4(iangle / 160., iangle / 60., iangle / 360., 0.);
    //get reverse
    float rev = -atan(vect.y, vect.x);
    rev = rev*20.;
    rev += iTime*20.;
    rev = (cos(rev + dist*100.) + 1.) * 180.;


    if (rev < iangle) fragColor = vec4(rev / 360., rev / 250., rev / 100., 0.);
}
