void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/(iResolution.xy - vec2(1.0,1.0));

    // Output to screen
    uv *=2.0;
    uv -= 1.0;

    uv.y *= iResolution.y/iResolution.x;
    float xcol=cos(uv.x*10.0*(sin(iTime)+2.0));
    float ycol=sin(uv.y*10.0*(sin(iTime)+2.0));

    fragColor.rb = vec2(mod(xcol+ycol,1.0));

}
