float spiralSDF(vec2 st, float t){
    st -= .5;
    float r = dot(st,st);
    float a = atan(st.y,st.x);
    return abs(sin(fract(log(r)*t+a*0.159)));
    }

float random (in vec2 st2){
    return fract (sin(dot(st2.xy, vec2(12.9898,78.233)))*43758.5453123);
}

float stroke(float x, float s, float w) {
    float d= step(s, x+w*.8) -
             step(s, x-w*.2);
    return clamp(d, 0., 0.2);
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord )

    {

         vec2 uv = 3.0 * fragCoord/iResolution.xy -1.0;
         uv.x *= iResolution.x/iResolution.y;
         vec3 color = vec3(random(uv),0.1,0.8);

         color += step(.7,spiralSDF(uv*iTime,.25));

         color += stroke(spiralSDF(uv*iTime,.25), .4, .2);

         fragColor = vec4(color,1.0);

     }
