renderpass.code:
float random(float x){
    return fract(sin(x)*336645.3452234);
}

vec2 random2(vec2 st){
    vec2 A = vec2(30.39534, 80.39583);
    vec2 B = vec2(34.39534, 81.39583);
    vec2 DOT = vec2(dot(st, A), dot(st, B));
    return fract(cos(DOT)*315732.432);
}

float reScale(float a1, float a2, float b1, float b2, float n){
    return b1 + ((n-a1)*(b2-b1))/(a2-a1);
}

float reflects(vec2 st){
    st -=0.5;
    float ranX = random(st.y);
    float rangeReflect = reScale(0., 1., 0.25, 0.4, cos(iTime)*ranX);
    float range = reScale(0., 6., -0.5, rangeReflect, cos(iTime)*6.);
    vec2 stRan = st * (cos(ranX+range));
    float c = length(stRan);
    c = step(c, 0.012);
    return c;
}

vec3 boardColor(vec2 st, vec3 color){

    vec2 position = vec2(-0.400,-.15) + vec2(fract(iTime*0.1)*1.4, 0.);
    vec2 stI = st - vec2(0.232,0.56)-position;
    vec2 stD = st- vec2(0.429,0.56)-position;
    vec2 stC = st- vec2(0.330,0.610)-position;
    vec2 stM = st- vec2(0.340,0.650)-position;
    vec2 stV = st- vec2(0.670,0.660)-position;

    //board
    float c = max(abs(stC.x), abs(stC.y/0.5));
    c = step(c, 0.032);

    float triI =-stI.x-stI.y;
    triI = step(triI, -0.1);
    float i = max(abs(stI.x), abs(stI.y));
    i = step(i, 0.067);
    i *= triI;

    float triD =stD.x-stD.y;
    triD = step(triD, -0.1);
    float d = max(abs(stD.x), abs(stD.y));
    d = step(d, 0.067);
    d *= triD;

    float base = i+c+d;
    color = mix(color, vec3(0.920,0.412,0.207), base);

    //Sail
    float triV =stV.x-stV.y;
    triV = step(triV, -0.300);
    float v = max(abs(stV.x*0.228), abs(stV.y/0.192));
    v = step(v, 0.076);
    v*=triV;

    color = mix(color, vec3(0.494,0.681,1.000), v);

    //mastil
    float m = max(abs(stM.x/0.156), abs(stM.y));
    m = step(m, 0.024);

    color = mix(color, vec3(0.990,0.821,0.205), m);

    return color;
}

float sun(vec2 st){
    st-=0.956;
    float c = length(st);
    float cSun = step(length(st),0.320);

    c = smoothstep(0.380, -0.004, c);
    return c * cSun;
}

vec3 backgroundColor(vec2 st){
    float red = reScale(-1., 1., 0.460, 0.8, cos(iTime*1.3));
    float blue = reScale(-1., 1., 0.460, 0.6, cos(iTime));

    vec3 A = vec3(0.101,blue,0.9);
    vec3 B = vec3(red,0.309,0.227);
    return  mix(A, B, st.y);
}

float frame(vec2 st){
    float ball = length(st-0.5);
    float range = reScale(-1., 1., 0.460, 0.528, cos(iTime));

    ball = step(range, ball);
    return ball;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;
    st -= vec2(0.4, 0.);

    //Fondo
    vec3 color = vec3(backgroundColor(st));
    //Reflects
    color = mix(color, vec3(1.000,0.814,0.094), reflects(st));
    //Board
    color = boardColor(st, color);
    //Sun
    color = mix(color, vec3(reScale(-1., 1., 0.660, 1., cos(iTime)),0.863,0.151), sun(st));
    //FrameBall
    color = mix(color, vec3(0.), frame(st));

    fragColor = vec4(color,1.0);
}
