vec3 colorA = vec3(0.033,0.302,0.912);
vec3 colorB = vec3(1.000,0.662,0.000);

float plot (vec2 st, float pct){
  return  smoothstep( pct-0.4, pct, st.y) -
          smoothstep( pct, pct+0.4, st.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = fragCoord.xy/iResolution.xy;
    vec3 color = vec3(0.0);

    vec3 pct = vec3(st.x);
    float streak = pow(st.x,0.5)+0.6;

    vec3 mirror = vec3(abs(st.y-0.25)*3.0);
    color = mix(colorA, colorB, pct);

    // sky + mirroring
    color = mix(vec3(0.297,0.305,0.455), color, mirror);

    // Plot transition lines for each channel
    color = mix(color, vec3(0.937,1.000,0.981), plot(st, streak));

    fragColor = vec4(color,1.0);
}
