#define R iResolution.xy

vec3 pattern(vec2 uv, float tile) {
    vec2 lv = smoothstep(0.0, 1.0, fract(sin(uv*tile)));
    float f = (lv.x*tile)-(lv.y*tile);
    float e = 1.0 - min(lv.x*tile, lv.y*tile);
    float a = smoothstep(-e, e, abs(cos(f)));
    float b = smoothstep(0.0, 1.0, tan(e*3.14));

    float v = mix(a, b, 0.5*(0.5+(0.5*sin(iTime))));
    return vec3(v);
}

void mainImage( out vec4 O, in vec2 fc )
{
    vec3 col = vec3(0.0);
    vec2 uv = fc / R.y;
    uv += vec2(cos(iTime*0.2), sin(iTime*0.2));
    col = pattern(uv, 8.0 + (4.0*(0.5+(0.5*sin(iTime*0.2)))));
    O = vec4(col, 1.0);
}
