const float d = 1.5;

float Distribution(float x) {
    return -0.000009766*x*x+0.00625*x;
    //-((1)/(102400)) x^(2)+((1)/(160)) x
}
float RandomD(float d) {
    return abs(sin(10.*d));
    //return
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x = fragCoord.x;
    float d1 = Distribution(iResolution.x/2.);
    float d2 = Distribution(iResolution.y/2.);

    if(Distribution(x) + RandomD(x+sin(iTime/70.)-cos(iTime/250.)) >= d) {
        fragColor = vec4(0.,0.,0.,1.);
    }
}
