void  mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 st = fragCoord/iResolution.xy;
    st.x *= iResolution.x/iResolution.y; //x座標の倍率をyに合わせる

    vec2 uv = st*2.-vec2(iResolution.x/iResolution.y,1.); //原点を中央に
    vec2 wx = vec2(pow(length(uv), 2.))*10.; //中央からの距離の二乗

    vec2 warp_uv =  mix(uv,uv*0.,length(wx)*tan(iTime)*.01)*10.; //wxでmix
	float color2 = step(fract((warp_uv.x)),0.1)+step(fract((warp_uv.y)),0.1); //格子に

    float r = 1.;
    float g = color2+.3;
    float b = .5;



    fragColor = vec4(r,g,b,1.0);
}
