void mainImage(out vec4 fc, in vec2 fCoord)
{
    vec2 u = fCoord.xy / iResolution.xy;
    vec2 wU = 2. * u;

    float d = length(wU);
    vec2 s = wU * 0.1 + 0.2 * vec2(cos(0.071 * iTime * 2. + d), sin(0.073 * iTime * 2. - d));

    vec3 wCol = texture(iChannel0, s).xyz * 2.0;
    float w = max(wCol.r, 0.85);

    vec2 offset = 0.01 * cos(wCol.rg * 3.14159);
    vec3 col = texture(iChannel1, u + offset).rgb * vec3(0.8, 0.8, 1.5);
    col *= w * 1.2;

    fc = vec4(mix(col, texture(iChannel1, u + offset).rgb, 0.5), 1.0);
}
