//Simplified version By FabriceNeyret2
void mainImage( out vec4 f, vec2 u ){

    vec2  R = iResolution.xy,
          U = vec2( u - .5*R ) / R.y, 
          A = abs(U/5.), V;         
    float t = cos(iTime/1.5+3.14) / 2.,
          N = 91.*abs(t) + 11., v;

    f *= 0.;

    for( float i = 1.; i < N; i += .1) {
            V = cos(t+i/11.) * vec2(cos(t*i),sin(t*i));
            if (length(U + V/4.) < i/111.) f++;
            v = 3.*length(U+V/2.);
            f    *= smoothstep(.005,.09, v - .012*abs(4.*U.y) );
            f.bg *= smoothstep(.01 ,.04, v - A ); 
    }

    f /= 8.*N; 
}
