vec4 dchar(sampler2D chan,vec2 p,int c)
{
vec2 dFdx=dFdx(p/16.0),dFdy=dFdy(p/16.0);
if ((p.x<0.0)||(p.x>1.0)||(p.y<0.0)||(p.y>1.0)) return(vec4(0,0,0,1e5));
return(textureGrad(chan,p/16.0+fract(vec2(c,15-c/16)/16.0),dFdx,dFdy));
}
