#define g(y,x,m) (y < 0. ? x < .6 : x > .4) && (m < .1 || m > .5 && m < .6 )

void mainImage( out vec4 C, in vec2 U )
{
    U = fract((U+vec2(99, 90))/181.+.4*iTime) - .5;

    vec2 m = fract(U + .3),
         t = fract(U + iTime * sign(U.yx) * vec2(-.4, .4));

    U.x = -U.x;


    C = vec4(.7*vec3(g(U.y, t.x, m.y) || g(U.x, t.y, m.x)) + vec3(.4, .6, .3), 1);
}
