float circleShape(vec2 uv, vec2 pos, float radius, float blur, float proportion ) {
    float center = distance(uv, pos);
    float circleShape = smoothstep(radius, radius + blur, center);
    return circleShape;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = gl_FragCoord.xy/iResolution.xy;
    float proportion = iResolution.x/iResolution.y;
    float pct = 0.0;
    vec3 color = vec3(0.1);

    // Circle - Yellow
    float jumpSpeed_A = 4.3;
    float weightA = abs(cos(iTime * jumpSpeed_A/2.0));
    float radius_A = mix(0.05, 0.1,weightA);
    float animatedBlur_A = (1.0 -pow(1.0- weightA, 0.5)) * 0.06 ;
    float posX_A = ((cos(iTime*jumpSpeed_A)+1.0 ) / 2.0) * 0.2+0.35;
    float posY_A = pow(abs(sin(iTime*jumpSpeed_A)), 0.7);
    vec3 colorFarA = vec3(1.0, 0.5, 0.2);
    vec3 colorNearA = vec3(0.09, 0.06, 0.04);
    vec3 colorA = mix(colorNearA, colorFarA, weightA); ///////////
    posY_A = mix(radius_A/proportion, 1.0-(radius_A/proportion), posY_A) - (pow(weightA, 0.5) * 0.05);
    float circleA = 1.0- circleShape(uv, vec2(posX_A, posY_A), radius_A, animatedBlur_A, proportion );

    // Circle - Blue
    float jumpSpeed_B = 5.0;
    float weightB = abs(sin(iTime * jumpSpeed_B/4.0));
    float radius_B = mix(0.05, 0.1,weightB);
    float animatedBlur_B = (1.0 -pow(1.0-weightB, 0.5)) * 0.08 ;
    float posX_B = ((cos(iTime*jumpSpeed_B/2.0)+1.0 ) / 2.0) * 0.5+0.25;
    float posY_B = pow(abs(sin(iTime*jumpSpeed_B)), 0.7);
    vec3 colorFarB = vec3(0.2, 0.3882, 1.0);
    vec3 colorNearB = vec3(0.04, 0.04, 0.07);
    vec3 colorB = mix (colorNearB, colorFarB, weightB);/////
    posY_B = mix(radius_B/proportion, 0.75-(radius_B/proportion), posY_B) - (pow(weightB,0.5) * 0.5)+0.32;
    float circleB = 1.0- circleShape(uv, vec2(posX_B, posY_B), radius_B, animatedBlur_B, proportion );

    // Inverting circle order using "zdepth"
    if (animatedBlur_A > animatedBlur_B){
        color = mix(color, colorB, circleB);
        color = mix(color, colorA, circleA);
    } else {
        color = mix(color, colorA, circleA);
        color = mix(color, colorB, circleB);


    }

    fragColor = vec4(color, 1.0);
}
