// Left click randomizes the CA state.

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
    int val = GET(fragCoord.xy, ivec2(0, 0));

    // The specifics of this rule lead to uneven distribution of cell values.
    // Hashing shuffles the values back to a near-uniform distribution.
    val = hash(val);

    fragColor.r = float(val & 255);
    fragColor.g = float((val >> 8) & 255);
    fragColor.b = float((val >> 16) & 255);

    fragColor.rgb /= 255.0;
}

// Simple time-dependent noise function for seeding the CA.
float noise(in vec2 co){
    int x = int(co.x * float(M));
    int y = int(co.y * float(M));
    int z = int(iDate.a);
    int w = int(fract(iDate.a) * float(M));
    int res = hash(x + hash(y + hash(z + hash(w))));

    return float(res);
}

// Main computation
void mainImage(out vec4 fragColor, in vec2 fragCoord) {        
    // Randomize initial state on 0th frame or if mouse button is pressed
    if (iFrame <= 0 || iMouse.z > 0.0) {
        fragColor.x = float(int(noise(fragCoord/iResolution.xy)) % M);
    } else {
    	ivec2 d;

        // Radius of the neighborhood.
        int R = 6;

        int minv = M - 1;
        int maxv = 0;

        for(d.y = -R; d.y <= +R; d.y++)
        	for(d.x = -R; d.x <= +R; d.x++){ 
                if(length(vec2(d)) > float(R)) continue;
                int s = GET(fragCoord, d);
                maxv = max(maxv, s);
                minv = min(minv, s);
             }

        // Changing how res is calcuated strongly affetcs th CA behavior. 
        int res = ((((minv | maxv) - 1) ^ ((minv & maxv) + 1))) + 1;

        for(d.y = -R; d.y <= +R; d.y++)
        	for(d.x = -R; d.x <= +R; d.x++){ 
                if(length(vec2(d)) > float(R)) continue;
                int s = GET(fragCoord, d) ^ res;
                maxv = max(maxv, s);
                minv = min(minv, s);
             }

        maxv ^= res;
        minv ^= res;

        // Changing this formula also has a big effect.
        res = maxv + minv;
        if (res % 3 == 0)
            res = minv;
        else
            res = maxv;

        fragColor.x = float(res);
    }
}

#define GET(coord, offset) int(texelFetch(iChannel0, ivec2(mod(coord + vec2(offset) + iResolution.xy, iResolution.xy)), 0).x)

#define M (1<<24)

int hash(int x) {
    x++;
    x ^= x >> 17;
    x *= 0xed5ad4bb;
    x ^= x >> 11;
    x *= 0xac4c1b51;
    x ^= x >> 15;
    x *= 0x31848bab;
    x ^= x >> 14;
    return x;
}
