vec3 FivePointStar(vec2 p, vec2 center, float rmin, float rmax, vec3 starColor)
{
    float npoints = 5.0;
    float starAngle = 2.0*3.1415926/npoints;
    vec3 p0 = rmax*vec3(cos(starAngle/2.0),sin(starAngle/2.0),0.0);
    vec3 p1 = rmin*vec3(cos(0.0),sin(0.0),0.0);
    vec3 d = p1-p0;
    p -= center;
    float r = sqrt(p.x*p.x+p.y*p.y);
    float angle = atan(p.y,p.x)+3.1415926-1.;
    float a = mod(angle,starAngle)/starAngle;
    if(a>=0.5)
        a = 1.0-a;
    vec3 d1 = r*vec3(cos(a*starAngle),sin(a*starAngle),0) - p0;
    float in_out = smoothstep(0.0,0.0001,cross(d,d1).z);
    vec3 col = mix(starColor, vec3(1.),in_out);
    return col;

}


vec4 drawRing(in vec2 uv, float c, float r)
{
    float sdfCircle = length(uv - c) - r; 
    vec3 col = (abs(sdfCircle) < 0.1 ? vec3(1,1,0) : vec3(1.));
    return vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float px = 2.0/iResolution.y;
    vec2 uv =(2.0 * fragCoord - iResolution.xy)/iResolution.y;

	float l = length(uv);

    float speed = smoothstep(0.0, 5.0, mod(iTime, 5.0));

    vec2 mouvCircle = vec2((sin(speed*2.0*3.1415926)*0.8),(cos(speed*2.0*3.1415926)*0.8));

    vec3 star = FivePointStar(uv,mouvCircle,0.1,0.04,vec3(1.0,0,0.0)); // signed distance function

    vec4 col = vec4(star, 1.0);

    fragColor = min(drawRing(uv, 0.0, 0.8), col);

}
