void mainImage(out vec4 O, vec2 U) {
  int x = int(U * 4. - iResolution.x*2.),
      y = int(U.y * 4. + iResolution.y*8.),
      r = (x)^(y);
   O = vec4( abs(r*r*r)/(y+int(cos(iTime / 3.2) + sin(iTime / 2.1)*cos(iTime)*5000.)) % 9770 < 1000 );
}
