// WARNING: For educational purpose only, please don't commercial this.

const float PI = 22. / 7.;
const float FACTOR = 0.12;
const float SPEED_SCALE = 2.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    vec2 waved_uv = uv + (sin(uv * 2. * PI) * FACTOR * sin(iTime * SPEED_SCALE));

    fragColor = texture(iChannel0, waved_uv);
}
