bool colorOfVideoIsGreenAtThisPixel(vec4 video)
{
    vec3 greenScreenColor = vec3(0, 1, 0);
    // This value affects how much of a green halo we see. If it's too high, we won't see the green halo as much, but Britney will get holes in her body.
    float threshold = 0.7;
    return distance(video.rgb, greenScreenColor) < threshold;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Compute UV coordinates, from 0 to 1
    vec2 uv = fragCoord.xy / iResolution.xy;

    // This line is retrieving texels from the iChannel0 recording.
    vec4 stuntman = texture(iChannel0, uv);

    // Check if the current pixel color is similar to the green screen color. If so, we replace it with the background.
    if (colorOfVideoIsGreenAtThisPixel(stuntman))
    {
        vec4 britney = texture(iChannel1, uv);
        fragColor = britney;
        vec4 thinkOfEngland = texture(iChannel2, uv);
        if (colorOfVideoIsGreenAtThisPixel(britney))
        {
            // replace Britney's green screen with London, so this song and swordfight has an epic setting
            fragColor = thinkOfEngland;
        }
    }
    else 
    {
        // Keep the original color.
        fragColor = stuntman;
    }

}


