#define MIN_SIZE 0.02
#define MAX_SIZE 0.5

// Draw dividing bars.
float dividingBar(float p,float start, float end){
    return 1.0 - step(start, p) * step(p, end);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float progress = fract(0.2 * iTime);

    vec4 origin = texture(iChannel0, uv);

    float luminance = 0.2126 * origin.r + 0.7152 * origin.g + 0.0722 * origin.b;

    // Time varying pixel color
    vec3 col = mix(vec3(luminance), origin.rgb, step(0.5, uv.x));

    col *= dividingBar(uv.x, 0.5, 0.5 + mix(MIN_SIZE, MAX_SIZE, 1.0 - progress));

    // Output to screen 
    fragColor = vec4(col,origin.a);
}
