void mainImage(out vec4 O, vec2 I)
{
    O *= 0.;
    vec2 r = iResolution.xy;
    for(float i,t=iTime;i<t/.1;)

        O+=(cos(i+vec4(6,1,2,3))+1.)*r.y/2e2/sqrt(t)/
        length(I+r*(abs(mod((t/.1-i++)/vec2(23.1,17),1.6)-.8)-.9));

    O=tanh(O*O);
}
