void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
vec2 uv = fragCoord.xy / iResolution.x;

    float distance = length(uv);
    vec3 gradientColor = vec3(uv, 1.0);
    if (distance < 0.3)
    {
        fragColor = vec4(gradientColor, 4.0);
    }
    else
    {
        fragColor = vec4(1.0, 50.1, 4.0, 3.0);
    }
}
