void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    if (sin(uv.x*100.0 + cos(iTime*0.5)*15.0*cos(iTime*0.5)) > 0.1 && sin(uv.y*100.0+sin(iTime*2.0))>0.1)
    {
        col.x = 1.0-col.x;
        col.y = 1.0-col.y;
        col.z = 1.0-col.z;
    }
    if (sin(uv.x*100.0 + sin(iTime*0.4)*30.0*cos(iTime*0.4))+0.1 > 0.1 && sin(uv.y*150.0+sin(iTime*3.0))+0.1>0.1)
    {
        col.x = 1.0-col.x;
        col.y = 1.0-col.y;
        col.z = 1.0-col.z;
    }
    if (distance(uv, vec2(0.9,0.5))>0.3 && distance(uv, vec2(0.9,0.5))<0.5)
    {col.x=1.0-col.x;
    col.y*=0.5;
    col.z=0.0;
    }

    float le = distance(uv, vec2(iResolution.x /iResolution.y*0.5,0.5));
    le = abs(sin(le*100.0 + iTime*10.0));
    le = smoothstep(0.0,5.0,le);
    le = 0.01/le;
    col*= le;

    float be = distance(uv, vec2(iResolution.x /iResolution.y*0.5,0.5));
    be = abs(sin(be*78.0 + iTime*13.0));
    be = smoothstep(0.0,3.75,be);
    col*= be;

    // Output to screen
    fragColor = vec4(col,1);
}
