float waveFunc(float s, float time)
{
    float f = 0.08;
    float t = 0.5;

    float wave = sin(s*0.41 + time*0.1415*t) * f;
    float wave2 = sin(s*0.7 + time*1.321*t) * f;
    float wave3 = sin(s*0.8 + time*0.791*t) * f;
    float wave4 = sin(s + time*0.42*t) * f;

    float w =wave + wave2 + wave3 + wave4;

    return w * sin(time*0.1);
}

float waveFuncD(float s, float time)
{
    float f = 0.08;
    float t = 0.5;

    float wave = cos(s*0.41 + time*0.1415*t) * f;
    float wave2 = cos(s*0.7 + time*1.321*t) * f;
    float wave3 = cos(s*0.8 + time*0.791*t) * f;
    float wave4 = cos(s + time*0.42*t) * f;

    float w =wave + wave2 + wave3 + wave4;

    return w * sin(time*0.1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float s = uv.x*50.*0.66;    

    float time = iTime * 50.;


    time += uv.x*100.;

    float w =waveFunc(s, time);;
    float wd =waveFuncD(s, time);


    uv.y += w;


    highp float d = abs(wd);

    float padding = (d*d)*1.0;

    float blend = 0.015;//*iResolution.y*0.001;
    float lineSize = 0.005;
    float line = smoothstep(lineSize+padding+blend, lineSize+padding,abs(uv.y-0.5));

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen

    float highlight = smoothstep(0.5, 12., (d*d*d*300.0))*3.5;
    float highlight2 = smoothstep(0.0, 0.5, (d*d*d*300.0))*0.4;
    float highlight3 = smoothstep(3.1, 30.0, (d*d*d*300.0))*100.;

    fragColor = vec4(col,1.0)*line*(0.6 + highlight + highlight2+ highlight3);//* (0.4 + min(0.6, abs(d*d*d)*20000.));

    //fragColor += vec4(highlight3);
}
