// https://iquilezles.org/articles/palettes/
vec3 palette(float t) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263, 0.416, 0.557);

    return a + b * cos(6.2318 * (c * t + d));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 11.0; i++) {
        uv = fract(uv * 1.6) - 0.5;

        float d = length(uv) * exp(-length(uv0));

        vec3 col = palette(length(uv0) + i * 0.002 + iTime * 0.04);

        d = cos(d * 5.0 + iTime) / 7.0;
        d *= abs(d/.4);
        d = pow(0.0009 / d, .9);

        finalColor += col * d;
    }

    // Apply audio-reactivity
    //float audioLevel = texture(iChannel1, vec2(0.5)).r;  // Fetch audio level from iChannel1

    float audioLevel =texture(iChannel1, vec2((0.5) / float(0.3))).r;
    finalColor *= audioLevel;

    // Enhance psychedelic colors
    finalColor = pow(finalColor, vec3(1.2, 1.5, 1.8));

    // Enhance kaleidoscope effect
    finalColor *= pow(.7, abs(cos(iTime)/1.2));
   // finalColor *= pow(.9, abs(atan(iTime))/.2);

    fragColor = vec4(finalColor, 1.0);
}
