vec3 palette( float t ) {
    vec3 a = vec3(0.778, 0.821, 0.161);
    vec3 b = vec3(0.411, 0.174, 0.796);
    vec3 c = vec3(0.763, 1.546, 1.164);
    vec3 d = vec3(2.480, 4.327, 0.050);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 iMouse = (iMouse.xy * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    vec3 col = vec3(0. ,0. ,0. );

    vec2 z0= uv;

    for(int i = 0; i < 500;i++){
         //square zn
        float new_X = (z0.x * z0.x) - (z0.y *z0.y);
        float new_Y= 2.0 * z0.x * z0.y; 
        //add c
        new_X += iMouse.x;
        new_Y += iMouse.y;
        z0 = vec2(new_X, new_Y);
        if(length(z0) > 2.0){
            col = palette(0.2*float(i)+0.05*(length(z0)-2.));
            break;
        }
    }  

    fragColor = vec4(col,1.0);
}
