void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 uv2 = fragCoord.xy / iResolution.xy;
    uv2.x += iTime*(1./3.)*100.;
    uv.x += iTime;
    uv.x += uv.y*sin(iTime*3.141);
    uv.y += sin(iTime*3.141*2.)/16.;
    uv.x = mod(uv.x,1.); // because weird shadertoy bug
    uv.y = mod(uv.y,1.); // also because weird shadertoy bug
    // shadertoy's noise has only 1 channel, red, but shader editor's has 3 channels (each is set to the same value though)
    float v = texture(iChannel1,uv2).x;
    vec4 noise = vec4(v);
    fragColor = (texture(iChannel0,uv)+noise/4.)/2.;
}
