/*
This is a drawing of a 732 triangle group's tiling. 
The quantities below were computed using the method given
in the source code of my hyperbolic tiling web app Curvascope:
https://github.com/zenzicubic/Curvascope
*/
const vec2 invCen = vec2(2.01219217261, 0.);
const vec2 refNrm = vec2(0.433883739118, -0.900968867902);
const float invRadSq = 3.04891733952;

vec3 getCol(vec2 p) {
    p = (2. * p - iResolution.xy) / iResolution.y;
    if (dot(p, p) > 1.) return vec3(.1);

    bool fund;
    float dotProd, dis, n = 0.;
    vec2 dir;
    for (int i = 0; i < 50; i ++) {
        fund = true;
        dir = p - invCen;
        dis = dot(dir, dir);
        if (dis < invRadSq) {
            p = invCen + dir * (invRadSq / dis);
            n ++;
            fund = false;
        }
        dotProd = dot(p, refNrm);
        if (dotProd < 0.) {
            p -= 2. * refNrm * dotProd;
            n ++;
            fund = false;
        }
        if (p.y < 0.) { 
            p.y *= -1.;
            n ++;
            fund = false;
        }
        if (fund) break;
    }
    return vec3(0.224, 0.286, 0.671) * (.3 + .7 * mod(n, 2.));
}

void mainImage( out vec4 fragColor, in vec2 pos) {
    vec3 col = getCol(pos);

    col += getCol(pos + vec2(.5));
    col += getCol(pos + vec2(-.5));
    col += getCol(pos + vec2(-.5, .5));
    col += getCol(pos + vec2(.5, -.5));
    col += getCol(pos + vec2(.5, 0.));
    col += getCol(pos + vec2(-.5, 0.));
    col += getCol(pos + vec2(0., .5));
    col += getCol(pos + vec2(0., -.5));
    col *= .11111;

    fragColor = vec4(col,1.0);
}
