float rad = 100.0;
float fociDist = 150.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    rad *= (2.0 + sin(iTime));
    fociDist *= (2.0 + sin(iTime));
    vec2 centre = iResolution.xy * 0.5;
    vec2 leftF = centre - vec2(fociDist * 0.5, 0);
    vec2 rightF = centre + vec2(fociDist * 0.5, 0);
    float d = ((length(leftF - fragCoord) + length(rightF - fragCoord)) * 0.5) - rad;
    d = clamp(d, 0.0, 1.0);
    float t = 1.0 - d;

    vec2 uv = fragCoord / iResolution.xy;
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    fragColor = vec4(col * t, 1.0);
}
