#define TWO_PI 3.1415926535

float rand(float n){return fract( sin(n) * 43758.5453123);}
float rand2(float n){return fract(cos(n) * 999742.235690);}

float noise(float p){
	float fl = floor(p);
    float fc = fract(p);
	return mix(rand(fl), rand(fl + 1.0), fc);
}

float noise2(float p){
	float fl = floor(p);
    float fc = fract(p);
	return mix(rand2(fl), rand2(fl + 1.0), fc);
}

float rect(float x){
    float d = 0.1;
    if(x < d){
       return x/d;
    }
    if(x < 1.-d){
        return 1.;
    }

    return (1.-x)/d;
}

float line(vec2 point, float t){
    float z = point.x*50. + t*10.;
    float k = rand(t) * rand(t*0.5);
    //float k = rand(rand2(iTime + point.x) * point.x * 0.1);
    float y = 0.5 + 0.25*(noise2(z*k)-noise(z*k))*rect(point.x);//0.5+0.3*sin(20.*TWO_PI*point.x);
    float r = length(point - vec2(point.x,y));
    return 0.25/r;
}


float line2(vec2 point, float t){
    float z = point.x*50.;
    float y =0.5 + 0.25*(noise2(z)-noise(z))*rect(point.x);
    float r = length(point - vec2(point.x,y));
    return 0.025/r;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(0.1,0.2,0.8)*vec3(line(uv,iTime));//0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}
