void mainImage( out vec4 O,in vec2 I)
{

    vec2 uv = (I* 2.0 - iResolution.xy) / iResolution.y;

    // create grid
    vec2 frac = fract(uv* 5.);
    float vertical_lines = step(frac.x, 0.1); 
    float horizontal_lines = step(frac.y, 0.1);
    float lines = vertical_lines + horizontal_lines;
    vec3 col = vec3(lines);

    // create circles
    float d = length(uv);
    d = sin(d*3. - iTime * 3.)/40.;
    d = abs(d);
    d = pow(0.005/d, 1.2);
    col *= d;


    O = vec4(col,1.0);
}
