void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Go from pixel coordinates, to numbers between 0 and 1
    // where (0.5,0.5) is the middle of the screen
    vec2 uv = fragCoord/iResolution.xy;

    // rgb
    vec3 redColor = vec3(1.0, 0.0, 0.0);
    vec3 greenColor = vec3(0.0, 1.0, 0.0);


    vec3 color;


    if (uv.x < 0.5){
        color = greenColor; // if left of screen, set green
    } else {
        color = redColor; // otherwise set red
    }


    // we add a 1.0 here to say that this color is opaque, rgba
    fragColor = vec4(color,1.0);
}
