const float PI = 3.14159265;
float atan2(float y, float x){
    return x == 0.0 ? sign(y) * PI / 2.0 : atan(y, x);
}
vec2 xy2pol(vec2 xy){
    return vec2(atan2(xy.y, xy.x), length(xy));
}
vec2 pol2xy(vec2 pol){
    return pol.y * vec2(cos(pol.x), sin(pol.x));
}
vec3 tex(vec2 st){
    float time = iTime;
    vec3 circ = vec3(pol2xy(vec2(time, 0.5)) + 0.5, 1.0);
    vec3[3] col3 = vec3[](
        circ.rgb, circ.gbr, circ.brg
    );
    st.t = 1.0;
    st.s = st.s / PI + 1.0;
    st.s += 0.8 * time;
    int ind = int(st.s);
    int ind2 = int(st.s + 1.0);
    vec3 col = mix(col3[ind % 2],col3[ind % 2 + 1],fract(st.t));
    return mix(col3[0], col, st.t);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord){
    vec2 pos = fragCoord.xy / iResolution.xy;
    pos = pos * 2.0 - vec2(1.0);
    pos *= vec2(20,20);
    float thr = 0.25 * sin(iTime);
    pos = floor(pos) + smoothstep(0.9 ,0.0 , fract(pos));
    pos = xy2pol(pos);    
    fragColor = vec4(tex(pos),1.0);
}
