void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 a_n_pixel_coords_norm = fragCoord.xy / iResolution.xy; 

    float n_aspect_ratio_width_to_height = iResolution.x / iResolution.y;
    float n_aspect_ratio_height_to_width = iResolution.x / iResolution.y;
    vec2 a_n_pixel_coords_norm_square_aspect_ratio = 
        vec2(
            ((fragCoord.x + ((iResolution.y - iResolution.x)/2.0)) / iResolution.y), 
            fragCoord.y / iResolution.y
        );

    float n_radius = 1.0;

    float n_tau = 6.2831;
    float n_amp = 0.01;
    vec2 a_n_translation = vec2(0.0,0.0);

    float n_distance = length(
        (0.2,0.5) - a_n_pixel_coords_norm_square_aspect_ratio
    );
    //n_distance -= mod(iTime*0.1, 1.0);
    //float n_mod_max = sin(3.14*a_n_pixel_coords_norm_square_aspect_ratio.x)*0.2;
    float n_mod_max = 0.2;
    float n_dist_mod = mod(n_distance, n_mod_max);
    float n_dist_mod_norm = n_dist_mod / n_mod_max;

    float n_r_start = 0.992;
    float n_r_end = 0.552;
    float n_r_range = n_r_start - n_r_end; 

    vec3 a_n_border = 
        vec3(
            246.0/255.0,
            176.0/255.0,
            153.0/255.0
        ); 
    float n_r = ((1.0-n_dist_mod_norm) * n_r_range + n_r_end); 

    if(n_r < 0.56){
        fragColor = vec4(a_n_border, 1.0);
    }else{
        fragColor = vec4(
            n_r,
            0.0,
            0.0, 
            1.0
        );
    }

    if(n_distance < 0.23){
        fragColor = vec4(0.0);
    }



}
