#define rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))
#define TIME (iTime/15.0)
float t(float a) {return (TIME / 4.1 * a);}
float t(float a, float s) {return (TIME * a) * s;}
float t(float o, float a, float s) {return o + sin(TIME / 2.4) - cos(TIME/3.1 * a + sin(TIME / 5.3)) * s;}

vec3 col = vec3(1);
vec3 acc = vec3(0);
float n = 20.;

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float luma(in vec4 color) {
    return dot(color.rgb, vec3(0.299, 0.587, 0.114));
}

float dither4x4(in vec2 position, in float brightness) {
    int x = int(mod(position.x, 4.0));
	int y = int(mod(position.y, 4.0));
	int index = x + y * 4;
	float limit = 0.0;

	if (x < 8) {
		if (index == 0) limit = 0.0625;
		if (index == 1) limit = 0.5625;
    	if (index == 2) limit = 0.1875;
    	if (index == 3) limit = 0.6875;
    	if (index == 4) limit = 0.8125;
    	if (index == 5) limit = 0.3125;
    	if (index == 6) limit = 0.9375;
    	if (index == 7) limit = 0.4375;
    	if (index == 8) limit = 0.25;
    	if (index == 9) limit = 0.75;
    	if (index == 10) limit = 0.125;
    	if (index == 11) limit = 0.625;
    	if (index == 12) limit = 1.0;
    	if (index == 13) limit = 0.5;
    	if (index == 14) limit = 0.875;
    	if (index == 15) limit = 0.375;
        limit *= 0.75;
  }

  return brightness < limit ? 0.0 : 1.0;
}

vec4 dither4x4(in vec2 position, in vec4 color) {
	return vec4(color.rgb * dither4x4(position, luma(color)), 1.0);
}

void mainImage(out vec4 fragColor, vec2 fragCoord){
    vec2 st = vec2(fragCoord - .5 * iResolution.xy) / iResolution.y;


    for(float i = 0.; i < n; i++){
        vec2 p = st;

        p *= n - i;                    // scale
        p *= rot(i * t(.4, .5, .1));   // rotate
		p.x += t(1.5);                 // translate

        vec2 id = mod(p + i, 4.);     // random color
        col = vec3(1.) / 4.;

        vec2 fr = fract(p) - .5;
        float shDir = t(i/n, 3.); // specular
        vec3 e = vec3(.5);
        col = pal(p.y, e, e, e, 0.35 * vec3(0.,0.33,0.66));
        col *= .3 
            + pow(4. 
                * abs(
                    dot(fr, .8 * vec2(cos(shDir), sin(shDir)))),
            12.);

        col *= .008 * i * i;     // distance fade

        //col *= pal(p.y+sin(shDir), e, e, e, 0.35 * vec3(0.,0.33,0.66));


        float r = .3;
        acc = mix(acc, col, 
            smoothstep(r, r-.03, length(fr)));

    }
    acc = dither4x4(fragCoord, vec4(acc, 1.)).xyz;
    acc = vec3(luma(vec4(acc, 1.)));

    fragColor = vec4(acc, 1.0);
}
