void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy - 0.5; 

    uv.x *= iResolution.x / iResolution.y;

    // adding some movement to the circles
    float time = iTime * 0.3 + uv.x * 10.0;
    float dist = sin(time) * 0.5 + 0.5;

    vec2 pos = uv * dist;
    vec2 cir = pos.xy*pos.xy;
    cir += sin(pos.x*18.0+iTime)/25.0*sin(pos.y*7.0+iTime*1.5)/1.0;
    cir += uv.x*sin(iTime)/16.0+uv.y*sin(iTime*1.2)/16.0;

    float circles = (sqrt(abs(cir.x+cir.y*0.5)*25.0)*5.0);

    // add some dynamic to colors
    float r = sin(circles + iTime)*0.5 + 0.5;
    float g = sin(circles + iTime/2.0)*0.5 + 0.5;
    float b = sin(circles + iTime/4.0)*0.5 + 0.5;

    fragColor = vec4(r,g,b,1.0);
}
