#define r(a)   mat2(cos(a+vec4(0,11,33,0)))          //

void mainImage( out vec4 C, vec2 U )
{
    float o=4.,i=0.,j=i,s,g=1.,k=.05;
    vec3 p,r,n,c;
    for(; i++ < 100.; g += o*k )
      { s = 1.;
        if(abs(o)<.0005)continue;
        r = iResolution, 
        n=r-r,
        p = 2.5*vec3( g* ( U - r.xy/2. ) / r.y, g-3. );
        p.y+=2.;
        p.xz *= r(.1*iTime+3.5);
        k *= 1.036;
        for(j=0.;j<15.;j++)p.y-=.15,o = min(o, length(p)-.16);
        p.yz *= r(-.4);
        for(;s<2.2; s/=.9 ){
            p.xz *= r(1.3),
            p.yz *= r(-.4),
            p.z = abs(p.z),          
            n = .06*sin(p+1.2).yzx*s,
            p = (p*s).yxz;
            if(s>1.5)p.x = abs(p.x); 
            for(j=0.;j<6.;j++)p = p-n,o = min(o, length(p)-.1);
            }                        
        o = min(o, length(p*vec3(1,.2,.5))-.2);
        }        
    o+=.06;
    c = log(1. + p*o);    
    C = vec4(c,1);	
}
