#define PI 3.14159265

vec3 palette(float t)
{
    vec3 a = vec3(.5);
    vec3 b = vec3(.5);
    vec3 c = vec3(1.);
    vec3 d = vec3(0.234, 0.345, 0.678);

    return a + b*cos(2.0*PI*(t*c+d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0*gl_FragCoord.xy - iResolution.xy) / iResolution.y;
    vec3 col = vec3(.05, 0.1, 0.4);

    float d = 0.0; 

    p.x += 0.5*cos(0.567*iTime);
    p.y += 0.5*sin(0.434*iTime);


    for (float i = 0.0; i < 48.0; i++)
    {

        float r = (0.05*float(i+1.0));

        d = length(p - vec2( (1.0 - 0.01*i) * 0.25*cos(i*0.1*iTime), 
                             (1.0 - 0.01*i) * 0.25*sin(i*0.1*iTime) )) - r;

        d = .0001 / (d*d);
        vec3 color = palette(0.125*p.x+0.125*p.y);

        col += d*color;
    }


    fragColor = vec4(col, 1.0);
}
