#define TWOPI 6.2831853
#define R vec2(1.,0.)

#define N 2.

vec2 cmul(vec2 z, vec2 c) {
    return vec2(z.x * c.x - z.y * c.y, z.x * c.y + z.y * c.x);
}

vec2 cdiv(vec2 z, vec2 c) {
    float r = dot(c, c);
    return vec2(z.x * c.x + z.y * c.y, z.y * c.x - z.x * c.y) / r;
}

vec2 cpow(vec2 z, vec2 p) {
    float a = atan(z.y, z.x);
    float lnr = 0.5 * log(dot(z,z));
    float m = exp(p.x * lnr - p.y * a);
    float angle = p.x * a + p.y * lnr + TWOPI;
    return vec2(cos(angle), sin(angle)) * m;
}

vec2 hyperbola(float t) {
    vec2 z = vec2(cos(t),sin(t));
    return cdiv(cpow(cpow(z,N*R)+R,1./N*R),z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0 * (fragCoord.xy - 0.5*iResolution.xy) / -iResolution.y;

    float t = iTime*0.2;
    vec3 col = vec3(1);
    for(float i = 1.; i <= 12.*N; i+=2.) {
        float l = length(uv);
        col *= (vec3(0.5+l*2.)-length(uv + hyperbola(t*sqrt(i)))*12.*N);
    }
    fragColor = vec4(1.-col,1.0);
}
